import React from "react";
import CounterItem from "./CounterItem";
import styled, { keyframes } from "styled-components";
import Card from "../UI/Card";

const Website_Data = [
  {
    id: "1",
    name: "google.com",
    type: "search",
    description: "Google's Search engine",
  },
  {
    id: "2",
    name: "bing.com",
    type: "search",
    description: "Microsoft's Search engine",
  },
  {
    id: "3",
    name: "CNN.com",
    type: "news",
    description: "Overall news platform, more democratic tailored",
  },
  {
    id: "4",
    type: "news",
    name: "foxnews.com",
    description: "A place where republicans search for news",
  },
  {
    id: "5",
    name: "NFL.com",
    type: "sports",
    description: "Everything Football!",
  },
  {
    id: "6",
    name: "espn.com",
    type: "sports",
    description: "Everything sports!",
  },

  {
    id: "7",
    name: "stackoverflow.com",
    description: "A common place for developers",
  },
];

const CounterWebsites = () => {
  return (
    <WebsiteSection>
      <Card>
        <UnorderedList>
          {Website_Data.map((item) => (
            <CounterItem
              key={item.id}
              id={item.id}
              name={item.name}
              description={item.description}
              type={item.type}
            ></CounterItem>
          ))}
        </UnorderedList>
      </Card>
    </WebsiteSection>
  );
};

const fadeIn = keyframes`
from {
    opacity: 0;
    transform: translateY(3rem);
  }

  to {
    opacity: 1;
    transform: translateY(0);
  }

`;

const WebsiteSection = styled.section`
  max-width: 60rem;
  width: 90%;
  margin: 2rem auto;
  animation: ${fadeIn} 1s ease-out forwards;
`;

const UnorderedList = styled.ul`
  list-style: none;
  margin: 10;
  padding: 0;
`;
export default CounterWebsites;
